sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var userName = current.parent.subject_person.user_name;
	var firstName = current.parent.subject_person.first_name;
	var lastName = current.parent.subject_person.last_name;
	var gender = current.parent.subject_person.gender;
	var managerName = current.parent.subject_person.manager.user_name;
	var title = current.parent.subject_person.title;
	var department = current.parent.subject_person.department.name;
	var email = current.parent.subject_person.email;
	var businessPhone = current.parent.subject_person.phone;
	var mobilePhone = current.parent.subject_person.mobile_phone;
	var homePhone = current.parent.subject_person.home_phone;
	var location = current.parent.subject_person.location.name;

	var emp_start_date=current.parent.subject_person_hr_profile.employment_start_date;
	var emp_end_date=current.parent.subject_person_hr_profile.employment_end_date;
	var employee_number=current.parent.subject_person_hr_profile.employee_number;
	var hr_profile_sys_id = current.parent.subject_person_hr_profile.sys_id;
	var task_sys_id=current.sys_id;

	var ae_setting = new GlideRecord('x_264253_alertente_alertenterprise_settings');
	ae_setting.query();
	var enableWorkflow = false;
	var ae_user_info = new GlideRecord('x_264253_alertente_alertenteprise_user_info');
	ae_user_info.initialize();
	ae_user_info.subject_person = current.parent.subject_person;
	ae_user_info.subject_person_hr_profile=current.parent.subject_person_hr_profile;

	while(ae_setting.next()){
		enableWorkflow = ae_setting.enable_workflow;
		if(!enableWorkflow){
			ae_user_info.approval_status="Not Required";
			ae_user_info.alert_status="Provisioning InProgress";
			//call AE Web Service
		}
		else{
			ae_user_info.approval_status="Peding Approval";
			ae_user_info.alert_status="Not Provisioned";
		}
		break;
	}



	gslog(subject_person);

})(current, previous);
